mapper.insert:

	SELECT LAST_INSERT_ID()

INSERT INTO
	merchants_role(
		License,Name,Code,All_Data_Authority,All_Admin_Authority,All_Front_Authority,Default_Page_Type,Default_Page_ID,
		Default_Page_Url,Secret_Level,Memo,Create_Time,Creator_ID,Update_Time,Updater_ID
	)
VALUES(
		#{license},#{name},#{code},#{allDataAuthority},#{allAdminAuthority},#{allFrontAuthority},#{defaultPageType},
		#{defaultPageId},#{defaultPageUrl},#{secretLevel},#{memo},#{createTime},#{creatorId},#{updateTime},#{updaterId}
)

mapper.update:
UPDATE merchants_role
Name=#{name}, CODE=#{code}, All_Data_Authority=#{allDataAuthority}, All_Admin_Authority=#{allAdminAuthority}, All_Front_Authority=#{allFrontAuthority}, Default_Page_ID=#{defaultPageId}, Default_Page_Type=#{defaultPageType}, Default_Page_Url=#{defaultPageUrl}, Secret_Level=#{secretLevel}, Memo=#{memo}, Updater_ID=#{updaterId}, Update_Time=#{updateTime}
WHERE ID=#{id}
