mapper.insert:

	SELECT LAST_INSERT_ID()

INSERT INTO
	merchants_user(
		Type,User_Type,Name,Nick_Name,Real_Name,User_Password,Org_ID,Sex,Order_ID,Org_ID,IP_Address,
		ID_Card,Phone_Number,Email,Position,Pic,Status,Create_Time,Creator_ID,Update_Time,Updater_ID
	)
VALUES(
	#{type},#{userType},#{name},#{nickName},#{realName},#{userPassword},#{orgID},#{sex},#{orderId},#{ipAddress},
	#{idCard},#{phoneNumber},#{email},#{position},#{pic},#{status},#{createTime},#{creatorId},#{updateTime},#{updaterId}
)

mapper.update:
UPDATE merchants_user
Name = #{name}, Real_Name =#{realName}, Nick_Name =#{nickName}, User_Password = #{userPassword}, User_Type = #{userType}, Sex =#{sex}, Order_ID = #{orderId}, Org_ID=#{orgId}, IP_Address=#{ipAddress}, Pic = #{pic}, Status =#{status}, ID_Card=#{idCard}, Position=#{position}, Phone_Number=#{phoneNumber}, Email=#{email}, Updater_ID =#{updaterId}, Update_Time =#{updateTime}
WHERE ID=#{id}
