mapper.insert:

	SELECT LAST_INSERT_ID()

INSERT INTO 
	templating_model(
		License,Bill_ID,Model_Name,Model_Code,Model_Type,Model_Path,Order_ID,Creator_ID,Create_Time,Updater_ID,Update_Time
	)
VALUES(
	#{license},#{billId},#{modelName},#{modelCode},#{modelType},#{modelPath},#{orderId},#{creatorId},#{createTime},#{updaterId},#{updateTime}
)

mapper.update:
UPDATE templating_model
Bill_ID = #{billId}, Model_Name = #{modelName}, Model_Code =#{modelCode}, Model_Type = #{modelType}, Model_Path =#{modelPath}, Order_ID = #{orderId}, Updater_ID = #{updaterId}, Update_Time = #{updateTime}
WHERE ID=#{id}
